import React, { useContext, useState } from "react"
import { Form, Modal, Spinner } from "react-bootstrap";
import {  Navigate } from "react-router-dom";

import { MyContext } from "../../customRedux/CustomRedux";
import { Button } from "../../components/UI/Button";
import { initialValues } from "../login/initialValues"

import { loginSelector } from "./selectors";
import { ROUTE_NAMES } from "../../routes/routeNames";
import { loginThunk } from "../login/thunks";
import { ErrorMessage, Formik } from "formik";
import { validationSchema } from "./validation";

const FormikField = ({
  field, 
  form, // also values, setXXXX, handleXXXX, dirty, isValid, status, etc.
  ...props
}) => (
  <Form.Group 
  className="mb-3"  
  controlId="exampleForm.ControlInput1"
>
  <Form.Label>Email</Form.Label>
  <Form.Control {...field} {...props}/>
  <ErrorMessage name={field.name}/>
</Form.Group>
 
);


export const Login = () => {
  const { store, dispatch } = useContext(MyContext);
  const { isAuth, errors, isLoading } = loginSelector(store);

  const handleSubmit = (values) => {
    console.log(values);
    //const body = {
      //email: email,
      //password: password,
    //};

    //loginThunk(dispatch, body);
  //};

  if (isAuth) return <Navigate to={ROUTE_NAMES.HOME}/>;

  return (
      <div className="login">
        <Formik initialValues={initialValues} onSubmit={handleSubmit}
        validationSchema={validationSchema}
        >
          {(formikProps) => {
            console.log(formikProps.errors.email);
            return <Modal show={true} >
            <Modal.Header >
              <Modal.Title>Войдите в систему</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <Form.Group 
                className="mb-3" 
                controlId="exampleForm.ControlInput1"
              >
                <Form.Label>Email</Form.Label>
                <Form.Control 
                type="email" 
                placeholder="email" 
                value={formikProps.values.email}
                onChange={formikProps.handleChange} 
                name={"email"}
                />
                {formikProps.errors.email && (
                <div className="form-error-message">
                {formikProps.errors.email}
                </div>)}
              </Form.Group>

              <Form.Group 
              className="mb-3" 
              controlId="exampleForm.ControlInput1">
                <Form.Label>Password</Form.Label>
                <Form.Control 
                type="password" 
                placeholder="password"
                value={formikProps.values.password}
                onChange={formikProps.handleChange} 
                name={"password"}  
                />
                {formikProps.errors.password && (
                <div className="form-error-message">
                {formikProps.errors.password }
                </div>)}
              </Form.Group>

              <div className="form-error-message">{errors}</div>          

            </Modal.Body>
            <Modal.Footer>
              <Button
                disabled={isLoading}
                variant="success" 
                onClick={formikProps.handleSubmit}
                >
                {isLoading ? <Spinner animation="border" /> : "Войти"}
              </Button>
            </Modal.Footer>
        </Modal>
          }}
        </Formik>
        
      </div>
  );
  };
};
export default Login;
