import React, { useContext, useState } from "react"
import { Form, Modal } from "react-bootstrap";
import {  Navigate } from "react-router-dom";

import { MyContext } from "../../customRedux/CustomRedux";
import { Button } from "../../components/UI/Button";

import { loginSelector } from "./selectors";
import { ROUTE_NAMES } from "../../routes/routeNames";
import AuthService from "../../services/AuthService";
import { loginFail, loginLoading, loginSuccess } from "./actions";


export const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const { store, dispatch } = useContext(MyContext);
  const { isAuth,  } = loginSelector(store);

  const handleSubmit = () => {
    dispatch(loginLoading())
    AuthService.login({
      email: email,
      password: password,
    })
    
      .then((response) => {
        dispatch(loginSuccess(response))
       
      })
      .catch(({response}) => {
        dispatch(loginFail(response?.data?.message))
      });
  };

  if (isAuth) return <Navigate to={ROUTE_NAMES.HOME}/>;

  return (
      <div className="login">
        <Modal show={true} >
            <Modal.Header >
              <Modal.Title>Войдите в систему</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <Form.Group 
                className="mb-3" 
                controlId="exampleForm.ControlInput1"
              >
                <Form.Label>Email</Form.Label>
                <Form.Control 
                type="email" 
                placeholder="email" 
                value={email}
                onChange={(e) => setEmail(e.target.value)} 
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                <Form.Label>Password</Form.Label>
                <Form.Control 
                type="password" 
                placeholder="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}  
                />
              </Form.Group>          

            </Modal.Body>
            <Modal.Footer>
              <Button variant="success" onClick={handleSubmit}>
                Войти
              </Button>
            </Modal.Footer>
        </Modal>
      </div>
  );
};

export default Login;
