import { ACTION_TYPES } from '../constants/actionTypes'
import { loginInitialStore } from '../pages/login/reducers'

export const initiaValues = {
  loginPage: loginInitialStore,
}

export function reducer(state, action) {
  switch (action.type) {
    case ACTION_TYPES.LOGIN_LOADING:
      return {
        ...state,
        loginPage: {
          ...state.loginPage,
          isLoading: true,
          errors: null,
        },
      };

    case ACTION_TYPES.LOGIN_SUCCESS:
      const {accessToken, ...userInfo} = action.payload
      return {
        ...state,
        loginPage: {
          ...state.loginPage,
          isLoading: false,
          isAuth: true,
          token: true,
          isAuth: true,
        },
      };  

    case ACTION_TYPES.LOGIN_FAIL:
      return {
        ...state,
        loginPage: {
           ...state.loginPage,
           isLoading: false,
           isAuth: false,
           errors: action.payload,
        },
      };        

    case ACTION_TYPES.LOGOUT:
      return {
        ...state,
        loginPage: {
          ...state.loginPage,
          isAuth: false,
        },
      }

    default:
      return state
  }
}
