import React, { useEffect } from 'react'
import './ProductInfo.css'
import { useParams } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { productInfoSelector } from './selectors'
import { getProductByIdThunk } from './thunks'
import { Loader } from '../../components/Loader/Loader'

export const ProductInfo = () => {
  const { productId } = useParams()
  const dispatch = useDispatch()
  const { productInfo, isLoading, errors } = useSelector(productInfoSelector);

  useEffect(() => {
    dispatch(getProductByIdThunk(productId));
  }, [dispatch, productId]);

  if (isLoading) {
    return <Loader />
  }

  if (!productInfo) {
    return null
  }

  if (errors) {
    console.error(errors);
  }

  return (
    <div className="container product-info">
        <div className="product-info-top">
            <div className="product-info-image">
                <img src={productInfo.image} alt="product"/>            
            </div>


            
            <div className="product-info-stats">
                {productInfo.stats.map((stat) => {
                    return (
                        <div key={stat.title} className="product-info-stat">
                                        
                            <div className="product-info-stat-title">
                                 {stat.title}
                            </div>
                            <div className="product-info-stat-value">
                                {stat.value}
                            </div>
                        </div>
                    );
                })}
                            
            </div>
            
        </div>
    </div>
  );
};
