import React, { useEffect, useState } from 'react';
import "./Products.css";
import { getProductsThunk } from './thunks';
import { productsSelector } from './selectors';
import Loader from '../../components/Loader/Loader';
import { Product } from './components/Product/Product';
import { Row } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { usePagination } from '../../hooks/usePagination';

export const Products = () => {
  const { currentPage, onPaginationClick } = usePagination(1);
  const {}= usePagination(1);
  const {errors, isLoading, products} = useSelector(productsSelector);
  const dispatch = useDispatch()
  
  useEffect(() => {
    dispatch(getProductsThunk({ page: currentPage}));         
  }, [dispatch, currentPage]);

  if (errors) {
    console.error(errors);
  }


  return (
    <div className="products container">
      {isLoading && <Loader/>} 

      <Row>          
        {products.map((product) => {
          return <Product key={product.id} product={product}/>;
        })}

        </Row>
        <Paginan currentPage={currentPage}/>
    </div>
  );
  
};
