import { ACTION_TYPES } from '../constants/actionTypes'
import { loginInitialStore } from '../pages/login/reducers'
import { productsInitialState } from '../pages/products/reducers';

export const initiaValues = {
  loginPage: loginInitialStore,
  productsPage: productsInitialState,
}

export function reducer(state, action) {
  switch (action.type) {
    case ACTION_TYPES.LOGIN_LOADING:
      return {
        ...state,
        loginPage: {
          ...state.loginPage,
          isLoading: true,
          errors: null,
        },
      };

    case ACTION_TYPES.LOGIN_SUCCESS:
      const {data: loginData} = action.payload
      const {accessToken, ...userInfo} = loginData;

      localStorage.setItem("token", accessToken);
      return {
        ...state,
        loginPage: {
          ...state.loginPage,
          isLoading: false,
          isAuth: true,
          token: accessToken,
          userInfo: userInfo,
        },
      };  

    case ACTION_TYPES.LOGIN_FAIL:
      return {
        ...state,
        loginPage: {
           ...state.loginPage,
           isLoading: false,
           isAuth: false,
           errors: action.payload,
        },
      };        

    case ACTION_TYPES.LOGOUT:
      localStorage.removeItem("token");

      return {
        ...state,
        loginPage: {
          ...state.loginPage,
          isAuth: false,
          errors: null,
          isLoading: false, 
          userInfo: {},
        },
      };

      //Products


      case ACTION_TYPES.PRODUCTS_LOADING:
        return {
          ...state,
          productPage: {
            ...state.productPage,
            isLoading: true,
            errors: null,
          },
        };

      case ACTION_TYPES.PRODUCTS_SUCCESS:
        const {data: productsData} = action.payload
        
        return {
          ...state,
          productPage: {
            ...state.productPage,
            isLoading: false,
            products: productsData,
            
          },
        };  

      case ACTION_TYPES.PRODUCTS_FAIL:
        return {
          ...state,
          productPage: {
            ...state.productPage,
            isLoading: false,
            products: [],
            errors: action.payload,
          },
        };        

      
    default:
      return state;
  }
}
