import { ACTION_TYPES } from "../../../constants/actionTypes";

export const productsInitialState = {
  products: [],
  isLoading: false,
  errors: null,
};

export const productsPageReducer = (state = productsInitialState, action) => {
  switch (action.type) {
    case ACTION_TYPES.PRODUCTS_LOADING:
        return {
          ...state,
          productsPage: {
            ...state.productsPage,
            isLoading: true,
            errors: null,
          },
        };

      case ACTION_TYPES.PRODUCTS_SUCCESS:
        const {data: productsData} = action.payload;
        
        return {
          ...state,
          productsPage: {
            ...state.productsPage,
            isLoading: false,
            products: productsData,
            
          },
        };  

      case ACTION_TYPES.PRODUCTS_FAIL:
        return {
          ...state,
          productsPage: {
            ...state.productsPage,
            isLoading: false,
            products: [],
            errors: action.payload,
          },
        };        

      
    default:
      return state;
  }
}
