import React, { useContext, useEffect } from 'react';
import "./Products.css";
import { getProductsThunk } from './thunks';
import { MyContext } from '../../customRedux/CustomRedux';
import { productsSelector } from './selectors';
import Loader from '../../components/Loader/Loader';
import { Product } from './components/Product/Product';
import { Row } from 'react-bootstrap';
import { useSelector } from 'react-redux';

export const Products = () => {
  const reduxStore = useSelector((state) => state);
  console.log();
  const {store, dispatch} = useContext(MyContext);
  const { isLoading, errors, products } = productsSelector(store);

  useEffect(() => {
    getProductsThunk(dispatch, { page: 1, limit: 20 });
         
  }, [dispatch]);

  if (errors) {
    console.error(errors);
  }

  console.log(products);  
  return (
    <div className='products container'>
      {isLoading && <Loader/>}

      <Row>
          {products.map(product => {
            return <Product key={product.id} product={product}/>;
          })}      
      </Row>
    </div>
  );
  
};
