import React, { useContext } from "react"
import { Form, Modal } from "react-bootstrap";
import {  Navigate } from "react-router-dom";

import { MyContext } from "../../customRedux/CustomRedux";
import { Button } from "../../components/UI/Button";
import { logoutAction } from "./actions";
import { loginSelector } from "./selectors";
import { ROUTE_NAMES } from "../../routes/routeNames";

export const Login = () => {
  const [email, setEmail] = useContext("");
  const [password, setPassword] = useContext("");

  const { store, dispatch } = useContext(MyContext);
  const { isAuth } = loginSelector(store);

  if (isAuth) return <Navigate to={ROUTE_NAMES.HOME}/>;

  return (
      <div className="login">
        <Modal show={true} >
            <Modal.Header >
              <Modal.Title>Войдите в систему</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <Form.Group 
                className="mb-3" 
                controlId="exampleForm.ControlInput1"
              >
                <Form.Label>Email</Form.Label>
                <Form.Control 
                type="email" 
                placeholder="email" 
                value={email}
                onChange={(e) => setEmail(e.targe)} 
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="password" />
              </Form.Group>          

            </Modal.Body>
            <Modal.Footer>
              <Button variant="success" onClick={() => dispatch(logoutAction())}>
                Войти
              </Button>
            </Modal.Footer>
        </Modal>
      </div>
  );
};

export default Login;
